mapper.select:
SELECT e.employee_id AS employeeId, e.employee_name AS employeeName, (SELECT c.detail_code_name FROM commoncodedetail c WHERE c.detail_code = e.employee_type_code) AS employeeType, (SELECT c.detail_code_name FROM commoncodedetail c WHERE c.detail_code = e.employee_status_code) AS employeeStatus FROM employeeinfo e WHERE use_yn = 'Y'
AND e.employee_type_code = #{employeeTypeCode}
AND e.employee_status_code = #{employeeStatusCode}
AND (e.employee_id LIKE '%${searchText}%' OR e.employee_name LIKE '%${searchText}%')
ORDER BY employee_name

mapper.update:

UPDATE employeeinfo
SET use_yn = 'N'
WHERE employee_id=#{employeeId}
