mapper.select:
SELECT r.res_serial_id AS "resSerialId", r.res_name AS "resName", r.mgmt_id AS "mgmtId", r.res_status_code AS "resStatusCode", r.introduction_date AS "introductionDate", r.use_yn AS "resUseYn", rc.res_class_id AS "resClassId", SUBSTR(rcl.path,5) AS "resClassFullName", i.install_place_sn AS "installPlaceSn", i.install_place_name AS "installPlaceName", concat(i.install_place_address, concat(' ', i.install_place_detail_address)) AS "installPlaceFullAddress", ip.ip AS "ip", ip.ip_desc AS "ipDesc" FROM resinfo r LEFT JOIN installplace i ON r.install_place_sn = i.install_place_sn LEFT JOIN resclass rc ON r.res_class_id = rc.res_class_id LEFT JOIN resipmapping rim ON r.res_serial_id = rim.res_serial_id LEFT JOIN ipinfo ip ON rim.ip_sn = ip.ip_sn LEFT JOIN ( SELECT level AS "level", res_class_name, sys_connect_by_path(res_class_name, ' -> ') AS "PATH" FROM resclass WHERE res_class_name = ( SELECT rc.res_class_name FROM resinfo r LEFT JOIN resclass rc ON r.res_class_id = rc.res_class_id ) START WITH res_class_id = 'HW_000000' CONNECT BY PRIOR res_class_id = upper_res_class_id ) rcl ON rc.res_class_name = rcl.res_class_name WHERE r.monitoring_yn = 'Y'
AND
AND author_name like #{author.name}
